mapper.select:

SELECT username, password, role
FROM member
WHERE username = #{username}

mapper.update:

UPDATE member
SET car_number = #{carNumber}
WHERE car_number IS NULL AND status = 'ACTIVE' AND member_id = #{memberId}


UPDATE member
SET password = #{password}, nickname = #{nickname}, phone_number = #{phoneNumber}, profile_car = #{carProfile}
WHERE username = #{username}
AND deleted_date IS NULL
AND status = 'ACTIVE'


UPDATE member
SET fcm_token = #{fcmToken, jdbcType=VARCHAR}
WHERE username = #{username}
  AND deleted_date IS NULL
  AND status = 'ACTIVE'
