mapper.insert:


        SELECT parking_seq.nextval AS parkingId FROM dual



INSERT INTO parking (
    parking_id, parking_lot_id, car_number, member_id
) VALUES (
    #{parkingId}, #{parkingLotId}, #{carNumber}, #{memberId, jdbcType=INTEGER}
