mapper.update:

UPDATE parking
SET status = 'EXIT'
WHERE parking_lot_id = #{parkingLotId}
  AND car_number = #{carNumber}
  AND status = 'PAY'


UPDATE parking
SET deleted_date = CURRENT_TIMESTAMP
WHERE parking_id = #{parkingId}
